    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
      String elementos =  this.campoItem.getText(); //armazenou o campoItem em uma vari√°vel
      lista.add(elementos); //adicionou a String elementos na ArrayList 
      campoItem.setText(null); //limpar o campo de texto, envia null
      String html = "<html>Lista de itens<br>";
      for(String item : lista){ //para cada item da lista, adcicione na resposta os mesmos
          html += item + "<br>"; //pediu para o html adicionar o item
      }
      resposta.setText(html);
    }//GEN-LAST:event_btnAddActionPerformed

